// Functions
function getBaseURL()
{
   if(basename($_SERVER['PHP_SELF'])=="index.php") {
      $folder = trim(dirname($_SERVER['PHP_SELF']), "/");
      $phpfile = (empty($folder) ? "/" : "/".$folder."/");
   } else {
      $phpfile = $_SERVER['PHP_SELF'];
   }
   return "http://".$_SERVER['SERVER_NAME'].$phpfile;
}

function titledwrap($title, $text, $maxlength=65)
{
   $title.=": ";
   $agentlines = explode("\n", wordwrap($text, ($maxlength-strlen($title)), "\n", true));
   
   $first=true;
   foreach($agentlines as &$agentline)
   {
      if($first)
      {
         $agentline = $title.$agentline;
         $first=false;
      }
      else
      {
         $agentline = str_repeat(" ", strlen($title)).$agentline;
      }
   }
   unset($agentline);
   return $agentlines;
}


// Build Data Array
$data = array(
   "clientip" =>$_SERVER['REMOTE_ADDR'],
   "clienthost" => gethostbyaddr($_SERVER['REMOTE_ADDR'])==$_SERVER['REMOTE_ADDR'] ? null : gethostbyaddr($_SERVER['REMOTE_ADDR']),
   "time" => date("c"),
   "timestamp" => time(),
   "useragent" => $_SERVER['HTTP_USER_AGENT'],
);

$validactions = array("usage", "png", "test", "favicon", "json", "xml", "serialize", "plain", "banner", "separated");
$baseurl = getBaseURL();





define("BASEURL", $baseurl);   
define("PNG_WIDTH", 468);
define("PNG_HEIGHT", 60);
define("PNG_BORDER_PADDING", 3);
define("PNG_TEXT_MAXLENGTH", 60);
define("BANNER_TEXT_MAXLENGTH", 65);
   
   
function action_png($data)
{
   $img = imagecreate(PNG_WIDTH, PNG_HEIGHT);
   imagecolorallocate($img, 255, 255, 255);
   
   $text_color = imagecolorallocate($img, 128, 170, 0);
   $black = imagecolorallocate($img, 0, 0, 0);
   
   imagerectangle($img, PNG_BORDER_PADDING, PNG_BORDER_PADDING, PNG_WIDTH-(PNG_BORDER_PADDING*2)+1, PNG_HEIGHT-(PNG_BORDER_PADDING*2)+1, $black);
   
   $i=10;
   $step=13;
   imagestring($img, 3, 10, $i,  "Your IP: ".$data['clientip'], $text_color);
   imagestring($img, 3, 10, $i+=$step,  "Hostname: ".(is_null($data['clienthost']) ? "No RDNS" : $data['clienthost']), $text_color);
   imagestring($img, 3, 10, $i+=$step,  (strlen($data['useragent'])>PNG_TEXT_MAXLENGTH?substr($data['useragent'], 0, PNG_TEXT_MAXLENGTH)."...":$data['useragent']), $text_color);

   ob_start();   
   imagepng($img);
   $data = ob_get_contents();
   ob_end_clean();
   imagedestroy($img);
   return $data;
}


function action_xml($data)
{
   $xml = "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<whatismyip>\n";
   foreach($data as $key => $value) {
      $xml .= "   <".$key.">".htmlentities($value)."</".$key.">\n";
   }
   $xml .= "</whatismyip>\n";
   return $xml;
}


function action_banner($data)
{
   $lines = array_merge(
      array("Your IP: ".$data['clientip']), 
      titledwrap("Hostname", (is_null($data['clienthost']) ? "No RDNS" : $data['clienthost']), BANNER_TEXT_MAXLENGTH), 
      titledwrap("Useragent", $data['useragent'], BANNER_TEXT_MAXLENGTH)
   );
   
   $maxlength=0;
   foreach($lines as $line)
   {
      if(strlen($line)>$maxlength)
      {
         $maxlength = strlen($line);
      }
   }
   
   $content = "";
   $content .= "\n";
   $content .= " +-".str_repeat('-', $maxlength)."-+\n";
   foreach($lines as $line)
   {
      $content .= " | ".$line.str_repeat(" ", $maxlength-strlen($line))." |\n";
   }
   $content .= " +-".str_repeat('-', $maxlength)."-+\n\n";
   $content .= " Server Host: ".BASEURL."\n";
   $content .= " Generated at ".$data['time']."\n\n";
   return $content;
}


function action_serialize($data)
{
   return serialize($data);
}


function action_json($data)
{
   return json_encode($data);
}

function action_separated($data)
{
   $names = array();
   $values = array();
   foreach($data as $name => $value)
   {
      $names[] = $name;
      $values[] = str_replace("|", "", $value);
   }
   return implode("|", $names)."\n".implode("|", $values);
}





$banner_output = htmlentities(action_banner($data));
$xml_output = htmlentities(action_xml($data));
$json_output = htmlentities(action_json($data));
$serial_output = htmlentities(action_serialize($data));
$separated_output = htmlentities(action_separated($data));

