$action = null;

// Check for GET Parameters
foreach($validactions as $validaction)
{
   if(isset($_GET[$validaction]))
   {
      $action = $validaction;
      break;
   }
}

// If no hit, check useragent
$agnt = strtolower($data['useragent']);
if(is_null($action) && (strpos($agnt, "wget")!==false || strpos($agnt, "curl")!==false))
{
   $action = "banner";
}

// If no hit again, set default
if(is_null($action))
{
   $action="usage";
}


// Output as PNG
if($action == "png") 
{
   header('Content-Type: image/png', true);
   echo action_png($data);
}
// Print Favicon 
else if($action == "favicon")
{
   header('Content-Type: image/x-icon', true);
   echo base64_decode($favicon);
}
// Output as JSON
else if($action == "json") 
{
   header('Content-type: application/json', true);
   echo action_json($data);
} 
// Output as XML
else if($action == "xml") 
{
   header('Content-type: application/xml', true);
   echo action_xml($data);
} 
// Output as serialize String
else if($action == "serialize") 
{
   header('Content-type: text/plain', true);
   echo action_serialize($data);
}
// Separated 
else if($action=="separated")
{
   header('Content-type: text/plain', true);
   echo action_separated($data);
}
// Output as plain
else if($action == "plain") 
{
   header('Content-type: text/plain', true);
   $getkey = (empty($_GET['key']) ? "" : trim($_GET['key']));
   echo (in_array($getkey, array_keys($data)) ? $data[$getkey] : $data['clientip']);
}
// Shell Banner
else if($action=="banner")
{
   header('Content-type: text/plain', true);
   echo action_banner($data);
}
else if($action=="usage") 
{
   echo $usage_text;
